import{_ as i,W as n,X as d,Y as e,Z as r,$ as s,a0 as t,G as h}from"./framework-f89bf418.js";const c="/ihpme-vue-deploy/assets/courses-frontend-402052d4.png",l="/ihpme-vue-deploy/assets/courses-external-listing-883988a3.png",p="/ihpme-vue-deploy/assets/courses-example-cbdef92d.png",o="/ihpme-vue-deploy/assets/courses-sidebar-810bb60e.png",u="/ihpme-vue-deploy/assets/courses-directory-e8ec8cf2.png",m="/ihpme-vue-deploy/assets/courses-editor-view-4e385468.png",f="/ihpme-vue-deploy/assets/courses-title-8818ae4f.png",g="/ihpme-vue-deploy/assets/courses-programs-c9d7d7a3.png",b="/ihpme-vue-deploy/assets/courses-select-program-224eddbc.png",w="/ihpme-vue-deploy/assets/courses-degrees-25a9ae84.png",y="/ihpme-vue-deploy/assets/courses-select-degrees-2011cdf0.png",x="/ihpme-vue-deploy/assets/courses-semester-offered-dbe12c97.png",v="/ihpme-vue-deploy/assets/courses-delivery-b8e34af7.png",_="/ihpme-vue-deploy/assets/courses-delivery-schedule-3d611b53.png",C="/ihpme-vue-deploy/assets/courses-notes-c885b82a.png",P="/ihpme-vue-deploy/assets/courses-more-info-967e91d8.png",E="/ihpme-vue-deploy/assets/courses-blank-new-71e16a68.png",k="/ihpme-vue-deploy/assets/courses-instructor-d852b9ac.png",T="/ihpme-vue-deploy/assets/courses-select-instructor-7dfdd2fd.png",D="/ihpme-vue-deploy/assets/courses-evaluation-95447ff4.png",q="/ihpme-vue-deploy/assets/courses-scheduled-class-47582ae2.png",S="/ihpme-vue-deploy/assets/courses-configuration-aa8bb628.jpg",A={},I=t('<h1 id="courses" tabindex="-1"><a class="header-anchor" href="#courses" aria-hidden="true">#</a> Courses</h1><p>Courses are stored within their own area of the WordPress editor and can be accessed and drawn into other areas of the IHPME website. This allows for a single editing and entry point across the website.</p><h2 id="examples" tabindex="-1"><a class="header-anchor" href="#examples" aria-hidden="true">#</a> Examples</h2><p>Courses appear in several areas throughout the IHPME website, such as in an overall directory, within the program details, or as parts of roadmaps. Below are a few examples of courses already populated within the IHPME website.</p><h3 id="course-directory-example" tabindex="-1"><a class="header-anchor" href="#course-directory-example" aria-hidden="true">#</a> Course Directory Example</h3>',5),W={href:"https://ihpme.utoronto.ca/courses/",target:"_blank",rel:"noopener noreferrer"},F=e("p",null,[e("img",{src:c,alt:"Course databases example"})],-1),M=e("h3",{id:"externally-linked-courses",tabindex:"-1"},[e("a",{class:"header-anchor",href:"#externally-linked-courses","aria-hidden":"true"},"#"),r(" Externally linked courses")],-1),N={href:"https://ihpme.utoronto.ca/programs/health-systems-research/msc-hsr-program-details/degree-requirements/",target:"_blank",rel:"noopener noreferrer"},H=t('<p><img src="'+l+'" alt="External courses in degree requirements"></p><h3 id="course-details-example" tabindex="-1"><a class="header-anchor" href="#course-details-example" aria-hidden="true">#</a> Course Details Example</h3><p>This is an example of a Course details page hosted on the IHPME website:</p><p><img src="'+p+'" alt="Example of course page"></p><h2 id="accessing-courses" tabindex="-1"><a class="header-anchor" href="#accessing-courses" aria-hidden="true">#</a> Accessing Courses</h2><p>To access <code>Courses</code> click or hover over the <code>Courses</code> link on the left-hand side of the WordPress editor. Here you will be presented with a several options.</p><h3 id="courses-editor-options" tabindex="-1"><a class="header-anchor" href="#courses-editor-options" aria-hidden="true">#</a> Courses editor options</h3><p>There are many options available within the <code>Courses</code> menu, most of which are relegated to administrator access only.</p><h4 id="example-of-wordpress-sidebar" tabindex="-1"><a class="header-anchor" href="#example-of-wordpress-sidebar" aria-hidden="true">#</a> Example of WordPress sidebar:</h4><p><img src="'+o+'" alt="Courses sidebar options"></p><h4 id="my-courses" tabindex="-1"><a class="header-anchor" href="#my-courses" aria-hidden="true">#</a> My Courses</h4><p>Provides a listing of all courses currently saved within the WordPress database.</p><h4 id="add-new" tabindex="-1"><a class="header-anchor" href="#add-new" aria-hidden="true">#</a> Add New</h4><p>Adds a new blank course to the database for editing.</p><h4 id="locations" tabindex="-1"><a class="header-anchor" href="#locations" aria-hidden="true">#</a> Locations</h4><p>Configures the listing of available locations where the courses will be administered.</p><h4 id="programs" tabindex="-1"><a class="header-anchor" href="#programs" aria-hidden="true">#</a> Programs</h4><p>Configures the listing of available programs within the WordPress database.</p><h4 id="degrees" tabindex="-1"><a class="header-anchor" href="#degrees" aria-hidden="true">#</a> Degrees</h4><p>Configures the listing of available degrees to be associated with the course.</p><h4 id="delivery-methods" tabindex="-1"><a class="header-anchor" href="#delivery-methods" aria-hidden="true">#</a> Delivery Methods</h4><p>Configures the available delivery methods for the courses.</p><h4 id="program-order" tabindex="-1"><a class="header-anchor" href="#program-order" aria-hidden="true">#</a> Program Order</h4><p>Configurable order of all programs currently available within IHPME.</p><h4 id="delivery-method-order" tabindex="-1"><a class="header-anchor" href="#delivery-method-order" aria-hidden="true">#</a> Delivery Method Order</h4><p>Configurable order of all delivery methods currently available within IHPME.</p><h2 id="editing-creating-courses" tabindex="-1"><a class="header-anchor" href="#editing-creating-courses" aria-hidden="true">#</a> Editing &amp; Creating Courses</h2><h3 id="adding-a-new-course" tabindex="-1"><a class="header-anchor" href="#adding-a-new-course" aria-hidden="true">#</a> Adding a new course</h3><p>To add a new course, select or hover over the <code>Courses</code> menu item on the left-hand sidebar and click &quot;Add New&quot;.</p><p><img src="'+o+'" alt="Courses sidebar options"></p><h3 id="finding-an-existing-course" tabindex="-1"><a class="header-anchor" href="#finding-an-existing-course" aria-hidden="true">#</a> Finding an existing course</h3><p>To find an existing course within the WordPress editor, select &quot;My Courses&quot; from the <code>Courses</code> sidebar menu. This will display a listing of all available courses in the WordPress editor.</p><p><strong>List view of all Courses directory:</strong></p><p><img src="'+u+'" alt="All courses directory"></p><p>From the list view of all courses you can filter the available results by several methods:</p><ul><li><strong>Search Field:</strong> Search for a specific course by name (upper right-hand corner)</li><li><strong>Dropdown filters:</strong> Provides several options to filter by date, program, degrees, and delivery methods</li><li><strong>By page:</strong> Allows jumping to a specific page or advancing forward/backwards within the listing of courses.</li></ul><h3 id="viewing-a-course" tabindex="-1"><a class="header-anchor" href="#viewing-a-course" aria-hidden="true">#</a> Viewing a Course</h3><p>Below is an example of an existing course within the WordPress editor. Courses are represented as a WSYIWIG (what you see is what you get) editor, providing a close to live view of how it will be represented on the published website. Content in most areas of a course can be edited directly as you would any other content within the WordPress editor.</p><p><img src="'+m+'" alt="Course editor view"></p><h3 id="editing-a-course" tabindex="-1"><a class="header-anchor" href="#editing-a-course" aria-hidden="true">#</a> Editing a Course</h3><h4 id="course-title" tabindex="-1"><a class="header-anchor" href="#course-title" aria-hidden="true">#</a> Course Title</h4><p>The current name of the course can be edited directly by changing the text in the top of the editor window. This will also change the text within the blue header and image area to match.</p><p><img src="'+f+'" alt="Course title area"></p><p>Within the header you can also add the following changes:</p><h4 id="course-code" tabindex="-1"><a class="header-anchor" href="#course-code" aria-hidden="true">#</a> Course Code</h4><p>Free-form text area for course code to be entered.</p><h4 id="programs-1" tabindex="-1"><a class="header-anchor" href="#programs-1" aria-hidden="true">#</a> Programs</h4><p>Allows tagging of the affiliated programs related to the course. Edit by selecting &quot;Edit&quot; in the upper-right hand corner.</p><p><img src="'+g+'" alt="Programs input area"></p><p><strong>Programs selection window:</strong></p><p><img src="'+b+'" alt="Programs selection window"></p><h4 id="degree" tabindex="-1"><a class="header-anchor" href="#degree" aria-hidden="true">#</a> Degree</h4><p>Select an associated degree from the the selection window.</p><p><img src="'+w+'" alt="Degree input area"></p><p><strong>Degrees selection window:</strong></p><p><img src="'+y+'" alt="Degrees selection window"></p><h4 id="semester-offered" tabindex="-1"><a class="header-anchor" href="#semester-offered" aria-hidden="true">#</a> Semester offered</h4><p>Free-form text area for semesters in which the course is offered.</p><p><img src="'+x+'" alt="Semesters offered area"></p><h4 id="delivery-format" tabindex="-1"><a class="header-anchor" href="#delivery-format" aria-hidden="true">#</a> Delivery Format</h4><p>Selection dropdown for delivery format of current course.</p><p><img src="'+v+'" alt="Delivery format dropdown"></p><h4 id="delivery-schedule" tabindex="-1"><a class="header-anchor" href="#delivery-schedule" aria-hidden="true">#</a> Delivery Schedule</h4><p>Free-form text area for input of the delivery schedule of the current course.</p><p><img src="'+_+'" alt="Delivery schedule input area"></p><h4 id="notes" tabindex="-1"><a class="header-anchor" href="#notes" aria-hidden="true">#</a> Notes</h4><p>Free-form text area for notes related to the course.</p><p><img src="'+C+'" alt="Notes input area"></p><h4 id="for-more-information" tabindex="-1"><a class="header-anchor" href="#for-more-information" aria-hidden="true">#</a> For more information</h4><p>Free-form text area for more information related to the course.</p><p><img src="'+P+'" alt="For more information input area"></p><h3 id="course-description-area" tabindex="-1"><a class="header-anchor" href="#course-description-area" aria-hidden="true">#</a> Course Description Area</h3><p>Within the course description area there are several predefined areas to populate when creating a new course. Each of these predefined areas allow for free-form editing of titles and content and are there to serve as a guide to the creation of course information. If no information is populated, these items will not display on the published page.</p><h4 id="new-course-example-page" tabindex="-1"><a class="header-anchor" href="#new-course-example-page" aria-hidden="true">#</a> New Course Example Page</h4><p><img src="'+E+'" alt="New course example page"></p><h4 id="prerequisite" tabindex="-1"><a class="header-anchor" href="#prerequisite" aria-hidden="true">#</a> Prerequisite</h4><p>Free-form text can be written here about any prerequisites for the current course.</p><h4 id="objectives" tabindex="-1"><a class="header-anchor" href="#objectives" aria-hidden="true">#</a> Objectives</h4><p>Intended for use of bulleted information about the overall course objectives.</p><h4 id="description" tabindex="-1"><a class="header-anchor" href="#description" aria-hidden="true">#</a> Description</h4><p>Free-form text can be written here to describe the course.</p><h4 id="instructors" tabindex="-1"><a class="header-anchor" href="#instructors" aria-hidden="true">#</a> Instructors</h4><p>This area is a preloaded tool that allows you to pull in profile images and links of affiliated course instructors into the course description.</p><p>Example of Instructors in a course description:</p><p><img src="'+k+'" alt="Example of instructors in course description"></p><p>To add a new instructor to the course description, hit the &quot;Add Profile&quot; text in the upper-right hand corner of the Instructors area. This will bring up the below image. Here you can search for an existing instructor name from the WordPress database. Multiple instructors can be added to a page and will automatically align themselves into a grid.</p><p><img src="'+T+'" alt="Adding a new instructor"></p><h4 id="evaluation-breakdown" tabindex="-1"><a class="header-anchor" href="#evaluation-breakdown" aria-hidden="true">#</a> Evaluation Breakdown</h4><p>This is a preformatted list intended for glanceable information. Values and descriptions can be written arbitrarily and will not total a proper sum of 100% (so check your math before publishing). Adding additional breakdown entries can be done by selecting the &quot;+ Add Breakdown Entry&quot; button at the bottom of the list.</p><p><img src="'+D+'" alt="Evaluation breakdown"></p><h4 id="competencies" tabindex="-1"><a class="header-anchor" href="#competencies" aria-hidden="true">#</a> Competencies</h4><p>Free-form text can be written here to describe the course.</p><h4 id="scheduled-classes" tabindex="-1"><a class="header-anchor" href="#scheduled-classes" aria-hidden="true">#</a> Scheduled Classes</h4><p>This area will be dynamically populated based on the timetable entries created for it. If no entries are present, this will not be displayed on the published page. See <a href="./getting-started/timetables">timetables</a> for more information about creating timetable entries.</p><p><img src="'+q+'" alt="Scheduled classes entry"></p><h2 id="configuration-panel" tabindex="-1"><a class="header-anchor" href="#configuration-panel" aria-hidden="true">#</a> Configuration Panel</h2><p>With the &quot;Course Title&quot; of the page selected, the configuration panel will present the following controls:</p><p><img src="'+S+'" alt="Course configuration panel example"></p><h3 id="available-controls" tabindex="-1"><a class="header-anchor" href="#available-controls" aria-hidden="true">#</a> Available controls</h3><h4 id="summary" tabindex="-1"><a class="header-anchor" href="#summary" aria-hidden="true">#</a> Summary</h4><p>The summary area contains basic information about the current page:</p><ul><li><strong>Visibility:</strong> Refers to who can view the page. Default is set to public.</li><li><strong>Publish:</strong> The date on which the course was published.</li><li><strong>URL:</strong> The address that the course is published to on the IHPME website.</li><li><strong>Template:</strong> Default template.</li><li><strong>Post format:</strong><ul><li>Link: used for external course details hosted on DSLPH or elsewhere.</li><li>Standard: Used for internally hosted course pages.</li></ul></li><li><strong>Move to trash button:</strong> Deletes the current course.</li></ul><h4 id="access-control" tabindex="-1"><a class="header-anchor" href="#access-control" aria-hidden="true">#</a> Access Control</h4><p>Allows for access of a specific profile name and associated UTORid email address. This feature is available to admin users only and allows login access for the associated user to edit their course.</p><h4 id="course-number" tabindex="-1"><a class="header-anchor" href="#course-number" aria-hidden="true">#</a> Course Number</h4><p>Free-form text area for course code to be entered.</p><h4 id="locations-1" tabindex="-1"><a class="header-anchor" href="#locations-1" aria-hidden="true">#</a> Locations</h4><p>Secondary toggle area for locations where the course is administered.</p><h4 id="programs-2" tabindex="-1"><a class="header-anchor" href="#programs-2" aria-hidden="true">#</a> Programs</h4><p>Secondary toggle area of affiliated programs for the current course</p><h4 id="degree-1" tabindex="-1"><a class="header-anchor" href="#degree-1" aria-hidden="true">#</a> Degree</h4><p>Secondary toggle area for delivery methods associated with the current course.</p>',112);function B(L,V){const a=h("ExternalLinkIcon");return n(),d("div",null,[I,e("p",null,[r("All courses (with the exception of externally linked courses) can also be found in the Course "),e("a",W,[r("database page"),s(a)]),r(".")]),F,M,e("p",null,[r("Within the degree requirements of some programs you will find courses denoted with an external link icon indicating that the details of the course are hosted elsewhere. "),e("a",N,[r("See page for reference"),s(a)])]),H])}const O=i(A,[["render",B],["__file","courses.html.vue"]]);export{O as default};
