import{_ as s,W as o,X as d,Y as i,Z as e,$ as h,a0 as r,G as l}from"./framework-f89bf418.js";const c="/ihpme-vue-deploy/assets/initiatives-frontend-5eeafe47.png",p="/ihpme-vue-deploy/assets/initiatives-example-bio-393d926d.png",a="/ihpme-vue-deploy/assets/initiatives-sidebar-e138c428.png",f="/ihpme-vue-deploy/assets/initiatives-directory-161135f8.png",u="/ihpme-vue-deploy/assets/initiatives-example-editor-14a7aff5.png",t="/ihpme-vue-deploy/assets/initiatives-blank-936d405e.png",g="/ihpme-vue-deploy/assets/initiatives-title-c9b184a1.png",v="/ihpme-vue-deploy/assets/initiatives-project-website-b099d4b8.png",m="/ihpme-vue-deploy/assets/initiatives-program-affiliations-e841191d.png",b="/ihpme-vue-deploy/assets/initiatives-program-affiliations-options-cd2a953c.png",w="/ihpme-vue-deploy/assets/initiatives-research-interests-cc6ef061.png",x="/ihpme-vue-deploy/assets/initiatives-research-interests-options-5a9ece2b.png",y="/ihpme-vue-deploy/assets/initiatives-lead-faculty-e070c7cd.png",I="/ihpme-vue-deploy/assets/initiatives-add-profile-178fce4b.png",_="/ihpme-vue-deploy/assets/initiatives-configuration-533cdcb3.png",P={},A=r('<h1 id="initiatives" tabindex="-1"><a class="header-anchor" href="#initiatives" aria-hidden="true">#</a> Initiatives</h1><p>Initiatives are stored within their own area of the WordPress editor. Initiatives stored in the WordPress database can be accessed and drawn into other areas such as profile on the IHPME website allowing for a single editing and entry point across the website.</p><h2 id="examples" tabindex="-1"><a class="header-anchor" href="#examples" aria-hidden="true">#</a> Examples</h2><p>Initiatives appear in several areas throughout the IHPME website, such as an overall directory or within profiles. Below are a few examples of courses already populated with the IHPME website.</p><h3 id="initiative-directory-example" tabindex="-1"><a class="header-anchor" href="#initiative-directory-example" aria-hidden="true">#</a> Initiative Directory Example</h3>',5),E={href:"https://ihpme.utoronto.ca/research/research-initiatives/",target:"_blank",rel:"noopener noreferrer"},k=r('<p><img src="'+c+'" alt="Research and initiatives page"></p><h3 id="initiatives-profile-feature-example" tabindex="-1"><a class="header-anchor" href="#initiatives-profile-feature-example" aria-hidden="true">#</a> Initiatives profile feature example</h3><p>Associations between people can be created allow display of affiliated initiatives within individual profiles on the IHPME website.</p><p><img src="'+p+'" alt="Initiatives profile feature example"></p><h2 id="accessing-initiatives" tabindex="-1"><a class="header-anchor" href="#accessing-initiatives" aria-hidden="true">#</a> Accessing Initiatives</h2><p>To access the <code>Initiatives</code> click or hover over the <code>Initiatives</code> link on the left-hand side of the WordPress editor. Here you will be presented with a several options.</p><h3 id="initiatives-editor-options" tabindex="-1"><a class="header-anchor" href="#initiatives-editor-options" aria-hidden="true">#</a> Initiatives editor options</h3><p>There are many options available within the <code>Initiatives</code> menu, most of which are relegated to administrator access only.</p><h4 id="example-of-wordpress-sidebar" tabindex="-1"><a class="header-anchor" href="#example-of-wordpress-sidebar" aria-hidden="true">#</a> Example of WordPress sidebar:</h4><p><img src="'+a+'" alt="Initiatives sidebar example"></p><h4 id="my-initiatives" tabindex="-1"><a class="header-anchor" href="#my-initiatives" aria-hidden="true">#</a> My Initiatives</h4><p>Provides a listing of all initiatives currently saved within the WordPress database.</p><h4 id="add-new" tabindex="-1"><a class="header-anchor" href="#add-new" aria-hidden="true">#</a> Add New</h4><p>Adds a new blank initiative to the database for editing.</p><h4 id="programs" tabindex="-1"><a class="header-anchor" href="#programs" aria-hidden="true">#</a> Programs</h4><p>Configures the listing of available programs within the WordPress database.</p><h4 id="research-interests" tabindex="-1"><a class="header-anchor" href="#research-interests" aria-hidden="true">#</a> Research Interests</h4><p>Configures the listing of available research interests available to within the WordPress editor</p><h4 id="program-order" tabindex="-1"><a class="header-anchor" href="#program-order" aria-hidden="true">#</a> Program Order</h4><p>Configurable order of all programs currently available within IHPME.</p><h4 id="research-interest-order" tabindex="-1"><a class="header-anchor" href="#research-interest-order" aria-hidden="true">#</a> Research Interest Order</h4><p>Configurable order of all research interests currently available within IHPME.</p><h2 id="editing-creating-initiatives" tabindex="-1"><a class="header-anchor" href="#editing-creating-initiatives" aria-hidden="true">#</a> Editing &amp; Creating Initiatives</h2><h3 id="adding-a-new-initiative" tabindex="-1"><a class="header-anchor" href="#adding-a-new-initiative" aria-hidden="true">#</a> Adding a new Initiative</h3><p>To add a new initiative, select or hover over the <code>Initiatives</code> menu item on the left-hand sidebar and click &quot;Add New&quot;.</p><p><img src="'+a+'" alt="Initiatives sidebar example"></p><h3 id="finding-an-existing-initiative" tabindex="-1"><a class="header-anchor" href="#finding-an-existing-initiative" aria-hidden="true">#</a> Finding an existing Initiative</h3><p>To find an existing initiative within the WordPress editor, select &quot;My Intiaitves&quot; from the <code>Initiatives</code> sidebar menu. This will display a listing of all available profiles in the WordPress editor.</p><p><strong>List view of all Initiatives directory:</strong></p><p><img src="'+f+'" alt="All initiatives directory"></p><p>From the list view of all profiles you can filter the available results by several methods:</p><ul><li><strong>Search Field:</strong> Search for a specific profile by name (upper right-hand corner).</li><li><strong>Dropdown filters:</strong> Provides several options to filter by date, program, degrees, accepting students, research interests.</li><li><strong>By page:</strong> Allows jumping to a specific page or advancing forward/backwards within the listing of profiles.</li></ul><h3 id="viewing-an-initiative" tabindex="-1"><a class="header-anchor" href="#viewing-an-initiative" aria-hidden="true">#</a> Viewing an Initiative</h3><p>Below is an example of an existing initiative within the WordPress editor. Initiatives are represented as a WSYIWIG (what you see is what you get) editor, providing a close to live view of how it will be represented on the published website. Content in most areas of a profile can be edited directly as you would any other content within the WordPress editor.</p><p><img src="'+u+'" alt="Initiatives editor example"></p><h3 id="creating-an-initiative" tabindex="-1"><a class="header-anchor" href="#creating-an-initiative" aria-hidden="true">#</a> Creating an Initiative</h3><p>When you creating a new initiative by selecting &quot;add new&quot; from the Initiatives sidebar you will be presented with a blank template for editing with several suggested fields.</p><h4 id="example-blank-initiative" tabindex="-1"><a class="header-anchor" href="#example-blank-initiative" aria-hidden="true">#</a> Example blank initiative</h4><p><img src="'+t+'" alt="New initiative example"></p><h4 id="initiative-title" tabindex="-1"><a class="header-anchor" href="#initiative-title" aria-hidden="true">#</a> Initiative Title</h4><p>The current name of the Initiative can be edited by directly changing the text in the top of the editor window. This will also change the text within the blue header and image area to match.</p><p><img src="'+g+'" alt="Initiative title area"></p><p>Within the header you can also add the following changes:</p><h4 id="subtitle" tabindex="-1"><a class="header-anchor" href="#subtitle" aria-hidden="true">#</a> Subtitle</h4><p>Free-form text area below the title area within the blue box for a subtitle description of the initiative. Optional usage, nothing will be displayed on the live website if left blank.</p><h4 id="project-website" tabindex="-1"><a class="header-anchor" href="#project-website" aria-hidden="true">#</a> Project Website</h4><p>If the Initiative has an external website address a link to that can be placed here.</p><p><img src="'+v+'" alt="Project website area"></p><h4 id="program-affiliation" tabindex="-1"><a class="header-anchor" href="#program-affiliation" aria-hidden="true">#</a> Program Affiliation</h4><p>Select an associated program affiliation for the current initiative by clicking &quot;edit&quot; in the upper right hand corner. Multiple programs can be affiliated with an initiative.</p><p><img src="'+m+'" alt="Program affiliations area"></p><p><strong>Program affiliations selection window:</strong></p><p><img src="'+b+'" alt="Program affiliations options"></p><h4 id="research-interests-1" tabindex="-1"><a class="header-anchor" href="#research-interests-1" aria-hidden="true">#</a> Research Interests</h4><p>This area allows selection of research interests affiliated with the current initiative. To define these select &quot;edit&quot; in the upper right hand corner of this area. Multiple items can be selected from the scrolling list pop-up that appears.</p><p><img src="'+w+'" alt="Research interests area"></p><p><strong>Research interests selection window:</strong></p><p><img src="'+x+'" alt="Research interests selection window"></p><h3 id="initiative-description-area" tabindex="-1"><a class="header-anchor" href="#initiative-description-area" aria-hidden="true">#</a> Initiative Description Area</h3><p>Within the initiative description area there are several predefined areas to populate when creating a new initiative. Each of these predefined areas allow for freeform editing of titles and content and are there to serve as a guide to the creation of initiative information. If no information is populated these items will not display on the published page.</p><h4 id="new-initiative-example-page" tabindex="-1"><a class="header-anchor" href="#new-initiative-example-page" aria-hidden="true">#</a> New Initiative Example Page</h4><p><img src="'+t+'" alt="New initiative example page"></p><h4 id="about" tabindex="-1"><a class="header-anchor" href="#about" aria-hidden="true">#</a> About</h4><p>Free form text can be written here about the initiative and its objectives. All elements of the block editor can be utilized here for rich text formatting.</p><h4 id="lead-faculty" tabindex="-1"><a class="header-anchor" href="#lead-faculty" aria-hidden="true">#</a> Lead Faculty</h4><p>This area is a preloaded tool that allows you to pull in profile images of already created profiles into the initiative. To select a profile hit the &quot;Add Profile&quot; button in the upper right hand corner of the &quot;Lead Faculty&quot; faculty area.</p><p><strong>Example of a faculty member added to an initiative:</strong></p><p><img src="'+y+'" alt="Lead faculty example"></p><p>After hitting &quot;Add Profile&quot; a new entry will be added in the lead faculty area. here you can search for an existing profile within the data base or create a new custom profile. When creating a custom profile no bio information will be associated with the profile card, only picture and title.</p><p><img src="'+I+'" alt="Add profile tool"></p><h4 id="affiliated-faculty" tabindex="-1"><a class="header-anchor" href="#affiliated-faculty" aria-hidden="true">#</a> Affiliated Faculty</h4><p>The affiliated faculty field operates in the same manner as the &quot;Lead Faculty&quot; area it only serves as a visual distinction between profile types.</p><h2 id="configuration-panel" tabindex="-1"><a class="header-anchor" href="#configuration-panel" aria-hidden="true">#</a> Configuration Panel</h2><p>With the &quot;Initiative&quot; of the page selected, the configuration panel will present the following controls:</p><p><img src="'+_+'" alt="Initiatives configuration panel"></p><h3 id="available-controls" tabindex="-1"><a class="header-anchor" href="#available-controls" aria-hidden="true">#</a> Available controls</h3><h4 id="summary" tabindex="-1"><a class="header-anchor" href="#summary" aria-hidden="true">#</a> Summary</h4><p>The summary area contains basic information about the current page:</p><ul><li>Visibility: Refers to who can view the page. Default is set to public.</li><li><strong>Publish:</strong> The date on which the profile was published.</li><li><strong>URL:</strong> Is the address that the profile is published to the IHPME website.</li><li><strong>Template:</strong> Default template.</li><li><strong>Post format:</strong><ul><li>Link: used for external initiatives with no detailed page hosted on the IHPME website</li><li>Standard: Used for internally hosted course pages</li></ul></li><li><strong>Move to trash button:</strong> Deletes the current profile.</li></ul><h4 id="access-control" tabindex="-1"><a class="header-anchor" href="#access-control" aria-hidden="true">#</a> Access Control</h4><p>Allows for access of a specific profile name and associated UTORid email address. This feature is available to admin users only and allows login access for the associated user to edit their profile.</p><h4 id="accepting-students" tabindex="-1"><a class="header-anchor" href="#accepting-students" aria-hidden="true">#</a> Accepting Students</h4><p>Enables a special flag that highlights this specific initiatives is currently accepting students.</p><h4 id="programs-1" tabindex="-1"><a class="header-anchor" href="#programs-1" aria-hidden="true">#</a> Programs</h4><p>Secondary toggle area of affiliated programs for the current initiative</p><h4 id="research-interests-2" tabindex="-1"><a class="header-anchor" href="#research-interests-2" aria-hidden="true">#</a> Research Interests</h4><p>Secondary toggle area of research interests for the current initiative</p><h4 id="feature-image" tabindex="-1"><a class="header-anchor" href="#feature-image" aria-hidden="true">#</a> Feature Image</h4><p>Allows assignment of a feature image or logo of the current initiative. If no image selected the default thumbnail be utilized.</p><h4 id="excerpt" tabindex="-1"><a class="header-anchor" href="#excerpt" aria-hidden="true">#</a> Excerpt</h4><p>An area for small descriptive copy about the current initiative, optional usage but encouraged.</p>',91);function q(W,T){const n=l("ExternalLinkIcon");return o(),d("div",null,[A,i("p",null,[e("All initiatives can also be found in the "),i("a",E,[e("Research and Initiatives page"),h(n)]),e(".")]),k])}const R=s(P,[["render",q],["__file","initiatives.html.vue"]]);export{R as default};
